import React from 'react';
import axios from "axios";
import { Link } from "react-router-dom";
import ItemPage from './itempage';




export default class ProductPage extends React.Component {
    state = {
        items: []
    }

    componentDidMount() {
        axios.get(`http://localhost:5000/collection/all`)
            .then(res => {
                const items = res.data;
                console.log(items)
                this.setState({ items });
            })
    }

    render() {
        return (
            <div className="products">
                {this.state.items.map(items =>
                    <div className='itemWrapper' key={items.id}>
                        <div className="imgwrapper">
                            <img src={items.img_urlOne} />
                        </div>
                        <div>
                            {items.name}
                        </div>
                        <div>
                            {items.price}
                        </div>
                        <div className='addToCart'>
                                <Link key={items.id}to={`/collection/${items.id}`}>Add to Cart</Link>



                        </div>


                    </div>)}

            </div>
        )
    }
}
